	function sendRequest($SmtpServer, $SmtpPort, $SmtpUser, $SmtpPass, $from, $to, $name, $subject, $body) {
		if (!$this->propertyLoader->smtpEnable) {
			return;
		}
		
		if ($SMTPIN = fsockopen ($SmtpServer, $SmtpPort)) 
		{
			fputs ($SMTPIN, "EHLO ".$SmtpServer."\r\n"); 
			$talk["hello"] = fgets ( $SMTPIN, 1024 ); 
			fputs($SMTPIN, "auth login\r\n");
			$talk["res"]=fgets($SMTPIN,1024);
			fputs($SMTPIN, base64_encode($SmtpUser)."\r\n");
			$talk["user"]=fgets($SMTPIN,1024);
			fputs($SMTPIN, base64_encode($SmtpPass)."\r\n");
			$talk["pass"]=fgets($SMTPIN,256);
			fputs ($SMTPIN, "MAIL FROM: ".$name." <".$from.">\r\n"); 
			$talk["From"] = fgets ( $SMTPIN, 1024 ); 
			fputs ($SMTPIN, "RCPT TO: <".$to.">\r\n"); 
			$talk["To"] = fgets ($SMTPIN, 1024); 
			fputs($SMTPIN, "DATA\r\n");
			$talk["data"]=fgets( $SMTPIN,1024 );			
			fputs($SMTPIN, "MIME-Version: 1.0\r\nContent-type: text/html; charset=utf-8\r\nTo: <".$to.">\r\nFrom: ".$name." <".$from.">\r\nSubject:".$subject."\r\n\r\n\r\n".$body."\r\n.\r\n");
			$talk["send"]=fgets($SMTPIN,256);
			//CLOSE CONNECTION AND EXIT ... 
			fputs ($SMTPIN, "QUIT\r\n"); 
			fclose($SMTPIN); 
			//
		} 
		return $talk;
	}
	
