	function sendOrderConfirmation($orderMadeBean, $locale) {
		$msg = $this->orderConfirmation[$locale];
		
		$msg = str_replace("{{order_number}}", $orderMadeBean->oSerial, $msg);
		$msg = str_replace("{{date}}", $orderMadeBean->oDate, $msg);
		$msg = str_replace("{{phone}}", $orderMadeBean->oPhone, $msg);
		$msg = str_replace("{{email}}", $orderMadeBean->oEmail, $msg);
		
		$subject = $this->orderConfirmationSubject[$locale];
		
		$subject = str_replace("{{order_number}}", $orderMadeBean->oSerial, $subject);
		
		$xmlLocale = $this->orderLocale[$locale];
		
		$this->createRequest($orderMadeBean->oEmail, $subject, $msg, $xmlLocale); 
	}
	
